import React from "react";
import Menu from "./Menu";
// import Menu from "./Menu";


export class PagesContent extends React.Component {
    constructor() {
        super();
        // this.props.greeting
    }

    getRootClassName(){
        var totn_string = 'page';
        return totn_string.concat(this.state.CurrPage);
    }

    render() {
        let listOfFunctions = [this.renderHome()];
        listOfFunctions[this.props.greeting];

    }

    renderHome(){
        return (
                <div
                        className={this.getRootClassName()}
                >
                    <Menu
                            OnChange={(p) => {
                                this.setState({CurrPage: p});
                            }}
                            CurrPage={this.state.CurrPage}
                    >
                    </Menu>
                    <PagesContent pageNumber={this.CurrPage}></PagesContent>
                </div>

        );
    }
}
