import React from "react";
import Menu from "./Menu";
// import Menu from "./Menu";


export class PagesContent extends React.Component {
    constructor() {
        super();
    }

    getClassName(){
        var totn_string = 'page';
        totn_string = totn_string.concat(this.state.CurrPage);
        return totn_string.concat("Content");
    }

    render() {
        let listOfFunctions = [this.renderHome()];
        return listOfFunctions[this.props.pageNumber];

    }

    renderHome(){
        return (
                <div
                        className={this.getClassName()}
                >

                    <h1>Хочеш собачку?</h1>
                    <div className="button">
                        <a href="" className="btn btn-one"> Чому пес?</a>
                        <a href="" className="btn btn-two"> Дізнатися більше</a>
                    </div>

                </div>

        );
    }
}
