import React from "react";


export class PropsMenu{
    OnChange = (page) => {};
    CurrPage = 0;
}

export default class Menu extends React.Component {
    constructor(props) {
        super();

        this.state = {
        };
    }

    
    renderSubMenus(curPage) {
        let result = [];
        for (let p of [0, 1, 2, 3]) {
            if(p === curPage){
                result.push(
                        (
                                <div>Current Page</div>
                        )
                );
            }else{
                result.push(
                        (
                                <div onClick={this.props.OnChange(p)}>{p}</div>
                                // <div>{p}</div>
                        )
                );

            }
        }
        return result;
    }


    render() {
        return (
                <div>
                    {this.renderSubMenus(this.props.CurrPage)}
                </div>
        );
    }
}
